import React from "react";
import styles from './users.module.css'
import * as axios from "axios";
import {setUsersAC} from "../../redux/usersReducer";

axios.get("https://social-network.samuraijs.com/docs/users").then(setUsers())

let Users = (props) => {
    if(props.users.length===0){
        props.setUsers([
            {
                id: 1,
                userPhoto: 'https://www.shutterstock.com/blog/wp-content/uploads/sites/5/2019/07/Man-Silhouette.jpg',
                followed: true,
                fullName: 'Artem B.',
                status: 'Code is art',
                location: {country: 'Ukraine', city: 'Mykolaiv'}
            },
            {
                id: 2,
                userPhoto: 'https://www.shutterstock.com/blog/wp-content/uploads/sites/5/2019/07/Man-Silhouette.jpg',
                followed: true,
                fullName: 'Lisa R.',
                status: 'Designer',
                location: {country: 'Ukraine', city: 'Kiev'}
            },
            {
                id: 3,
                userPhoto: 'https://www.shutterstock.com/blog/wp-content/uploads/sites/5/2019/07/Man-Silhouette.jpg',
                followed: false,
                fullName: 'Igor K.',
                status: 'Gaming is a sport!',
                location: {country: 'Ukraine', city: 'Gnivan'}
            },
            {
                id: 4,
                userPhoto: 'https://www.shutterstock.com/blog/wp-content/uploads/sites/5/2019/07/Man-Silhouette.jpg',
                followed: false,
                fullName: 'Pasha Ch.',
                status: "It's not my problems",
                location: {country: 'Ukraine', city: 'Ternopil'}
            },
            // {id: 5, fullName: 'Sasha Ch.', status: 'Code is art', location: {country:'Ukraine', city: 'Smila'}},
            // {id: 6, fullName: 'Sasha K.', status: 'Code is art', location: {country:'Ukraine', city: 'Mukachevo'}},
            // {id: 7, fullName: 'Vania S.', status: 'Code is art', location: {country:'Ukraine', city: 'Simakivka'}}
        ])
    }
    return (<div>
        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                        <img src={u.userPhoto} alt="avatar" className={styles.userPhoto}/>
                    </div>
                    <div>
                        {u.followed ? <button onClick={() => props.unfollow(u.id)}>Unfollow</button>
                            : <button onClick={() => props.follow(u.id)}>Follow</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.fullName}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{u.location.country}</div>
                        <div>{u.location.city}</div>
                    </span>
                </span>
            </div>)
        }
    </div>)
}
export default Users;
