import profileReduser from "./profileReduser";
import dialogsReduser from "./dialogsReduser";
import sideBarReduser from "./sideBarReduser";
import newsReduser from "./newsReduser";
import musicReduser from "./musicReduser";
import settingsReduser from "./settingsReduser";

const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const SEND_MESSAGE = 'SEND-MESSAGE';
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT';

let store = {
    _state :{
        profilePage:{
            posts:[
                {text:'Hi, how are you?', id:'1', likes: 15, img: 'https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg'},
                {text:'Oh, thanks. I\'m fine)', id:'2', likes: 25, img: 'https://shapka-youtube.ru/wp-content/uploads/2020/07/letter-s-1.jpg'}
            ],
            newPostText : 'newText'
        },
        dialogsPage: {
            dialogs: [
                {name: 'Igor', id: '1', img: 'https://i.pinimg.com/236x/74/05/5f/74055f83bfbdc20fdc1f9d1fc116fd26.jpg'},
                {name: 'Alexandr', id: '2', img: 'https://i.pinimg.com/originals/9c/77/46/9c7746225873e02d83b9315501b8dd2f.jpg'},
                {name: 'Sasha', id: '3', img: 'https://shapka-youtube.ru/wp-content/uploads/2020/07/letter-s-1.jpg'},
                {name: 'Pasha', id: '4', img: 'https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg'},
                {name: 'Yana', id: '5', img: 'https://i.pinimg.com/236x/7c/ab/18/7cab184c5e791921ed607e23458492b6.jpg'}
            ],
            messages: [
                {message: 'Hello', id: '1'},
                {message: 'How are you?', id: '2'},
                {message: 'You are shit!', id: '3'}
            ],
            newMessageText: 'newMessage'
        },
        sideBar:{
            online:[
                {img: 'https://i.pinimg.com/236x/7c/ab/18/7cab184c5e791921ed607e23458492b6.jpg'},
                {img: 'https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg'},
                {img: 'https://shapka-youtube.ru/wp-content/uploads/2020/07/letter-s-1.jpg'}
            ]
        }
    },
    _callSubscriber(){
    },

    subscribe (observer){
        this._callSubscriber = observer
    },
    getState(){
        return this._state
    },

    dispatch(action) {
        this._state.profilePage = profileReduser(this._state.profilePage,action)
        this._state.dialogsPage = dialogsReduser(this._state.dialogsPage,action)
        this._state.sideBar = sideBarReduser(this._state,action)
        newsReduser(this._state,action)
        musicReduser(this._state,action)
        settingsReduser(this._state,action)
    }
}
export const addPostActionCreator = () => ({type : ADD_POST})
export const updateNewPostTextActionCreator = (text) => ({ type: UPDATE_NEW_POST_TEXT, newText: text})
export const sendMessageActionCreator = () => ({ type : SEND_MESSAGE })
export const updateNewMessageTextActionCreator = (text) => ({ type: UPDATE_NEW_MESSAGE_TEXT, newText: text})
window.store = store;
export default store;
