import React from "react";
import s from "./Dialog.module.css"
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {Redirect} from "react-router";
import {Field} from "redux-form";

const Dialog = (props) => {
    let dialogElements = props.dialogsPage.dialogs.map(dialog =>
        <DialogItem name={dialog.name} id={dialog.id} key = {dialog.id} img={dialog.img}/>);
    let dialogMessages = props.dialogsPage.messages.map(message =>
        <Message message={message.message} key = {message.id}/>)

    let newMessage = React.createRef();
    let addMessage = () => {
        props.addMessage();
    }
    let onMessageTextChange = () => {
        let text = newMessage.current.value
        props.updateNewMessageText(text)
    }
    if(!props.isAuth){
        return <Redirect to={"/login"}/>
    }
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogElements}
                <div className={s.messages}>
                    {dialogMessages}

                </div>
            </div>
        </div>
    );
}
const AddMessageForm = (props) =>{
    return<>
        <form onSubmit={props.handleSubmit}>
            <Field placeholder="Enter message" onChange={onMessageTextChange} ref={newMessage} value={props.dialogsPage.newMessageText} />
            <button onClick={addMessage}>Post</button>
        </form>
    </>
}
export default Dialog
