import React from "react";
import styles from './users.module.css'

let Users = (props) => {
    props.setUsers([
        {
            id: 1,
            userPhoto: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fcommons.wikimedia.org%2Fwiki%2FFile%3AUser_Avatar_2.png&psig=AOvVaw0KhBm6H9uajNtdtGm-G_bc&ust=1610571099045000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMjcnIajl-4CFQAAAAAdAAAAABAJ',
            followed: true,
            fullName: 'Artem B.',
            status: 'Code is art',
            location: {country: 'Ukraine', city: 'Mykolaiv'}
        },
        {
            id: 2,
            userPhoto: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fcommons.wikimedia.org%2Fwiki%2FFile%3AUser_Avatar_2.png&psig=AOvVaw0KhBm6H9uajNtdtGm-G_bc&ust=1610571099045000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMjcnIajl-4CFQAAAAAdAAAAABAJ',
            followed: true,
            fullName: 'Lisa R.',
            status: 'Designer',
            location: {country: 'Ukraine', city: 'Kiev'}
        },
        {
            id: 3,
            userPhoto: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pinterest.com%2Fpin%2F823736588076106083%2F&psig=AOvVaw1DvbClk5GgR205C5n_W1Eu&ust=1610570293424000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCIDk5oagl-4CFQAAAAAdAAAAABAd',
            followed: false,
            fullName: 'Igor K.',
            status: 'Gaming is a sport!',
            location: {country: 'Ukraine', city: 'Gnivan'}
        },
        {
            id: 4,
            userPhoto: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fcommons.wikimedia.org%2Fwiki%2FFile%3AUser_Avatar_2.png&psig=AOvVaw0KhBm6H9uajNtdtGm-G_bc&ust=1610571099045000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMjcnIajl-4CFQAAAAAdAAAAABAJ',
            followed: false,
            fullName: 'Pasha Ch.',
            status: "It's not my problems",
            location: {country: 'Ukraine', city: 'Ternopil'}
        },
        // {id: 5, fullName: 'Sasha Ch.', status: 'Code is art', location: {country:'Ukraine', city: 'Smila'}},
        // {id: 6, fullName: 'Sasha K.', status: 'Code is art', location: {country:'Ukraine', city: 'Mukachevo'}},
        // {id: 7, fullName: 'Vania S.', status: 'Code is art', location: {country:'Ukraine', city: 'Simakivka'}}
    ])
    return (<div>
        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                        <img src={u.userPhoto} alt="avatar" className={styles.userPhoto}/>
                    </div>
                    <div>
                        {u.followed ? <button onClick={() => props.unfollow(u.id)}>Unfollow</button>
                            : <button onClick={() => props.follow(u.id)}>Follow</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.fullName}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{u.location.country}</div>
                        <div>{u.location.city}</div>
                    </span>
                </span>
            </div>)
        }
    </div>)
}
export default Users;
