const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
let initialStore = {
    posts:[
        {text:'Hi, how are you?', id:'1', likes: 15, img: 'https://whatsism.com/uploads/posts/2018-07/1530546770_rmk_vdjbx10.jpg'},
        {text:'Oh, thanks. I\'m fine)', id:'2', likes: 25, img: 'https://shapka-youtube.ru/wp-content/uploads/2020/07/letter-s-1.jpg'}
    ],
    newPostText : ''
}
const profileReducer = (state = initialStore, action) => {
    switch (action.type) {
        case ADD_POST: {
            let stateCopy = {...state}
            let newPost = {
                text: state.newPostText,
                id: 5,
                likes: 0,
                img: 'https://i.pinimg.com/originals/9c/77/46/9c7746225873e02d83b9315501b8dd2f.jpg'
            }
            stateCopy.posts = [...state.posts]
            stateCopy.posts.push(newPost);
            stateCopy.newPostText = '';
            return stateCopy
        }
        case UPDATE_NEW_POST_TEXT: {
            let stateCopy = {...state}
            stateCopy.newPostText = action.newText
            return stateCopy
        }
        default:
            return state
    }
}

export const addPostActionCreator = () => ({type : ADD_POST})
export const updateNewPostTextActionCreator = (text) => ({ type: UPDATE_NEW_POST_TEXT, newText: text})

export default profileReducer;
