import React, {useState} from "react";

const ProfileStatusWithHooks = (props) => {
    const [editMode, setEditMode] = useState(false)
    const [status, setStatus] = useState(props.status)
    const modeActivate = () => setEditMode(true)
    const deactivateEdit = () => setEditMode(false)
    onStatusChange = (e) => setState(e.currentTarget.value)

    return <>
        {editMode && <div>
            <input autoFocus={true} onBlur={deactivateEdit} type="text"/>
        </div>}
        {!editMode &&
        <div>
            <span onDoubleClick={modeActivate}> {props.status || "No status"}</span>
        </div>
        }
    </>


}

export default ProfileStatusWithHooks
