import React from "react";
import s from './ProfileInfo.module.css'

class ProfileStatus extends React.Component {
    state = {
        editMode:false
    }
    changeEditMode=()=>{
        this.state.editMode=true
        this.forceUpdate()
    }
    render() {
        return <>
            {!this.state.editMode
                ?<div><span onDoubleClick={this.changeEditMode.bind(this)}>{this.props.status}</span></div>
                :<div><input value={this.props.status} type="text"/></div>
            }
        </>
    }

}

export default ProfileStatus
