import React from "react";
import s from './MyPosts.module.css'
import Post from "./Post/Post";

const MyPosts = (props) => {
    debugger
    let postElements = props.posts.map(s => <Post messages = {s.text} like = {s.likes} img = {s.img}/>);
    let newPostElement = React.createRef();
    let onAddPost = () =>{
        props.addPost()
    }
    let onPostChange = () =>{
        let text = newPostElement.current.value
        props.updateNewPostText(text)

    };
    return (
        <div className={s.posts}>
            <div>
                <div>
                    <textarea onChange={onPostChange} ref={newPostElement} value={props.newPostText} placeholder="Text of new post"/>
                </div>
                <button onClick={onAddPost}>Add post</button>
            </div>
            <div>
                {postElements}
            </div>
        </div>
    );
}
export default MyPosts;
