import React from "react";
import s from "./Dialog.module.css"
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {sendMessageActionCreator, updateNewMessageTextActionCreator} from "../../redux/dialogsReducer";

const Dialog = (props) => {
    debugger
    let dialogElements = props.state.dialogs.map(dialog =>
        <DialogItem name={dialog.name} id={dialog.id} img={dialog.img}/>);
    let dialogMessages = props.state.messages.map(message =>
        <Message message={message.message}/>)

    let newMessage = React.createRef();
    let addMessage = () => {
        props.dispatch(sendMessageActionCreator());
    }
    let onMessageTextChange = () => {
        let text = newMessage.current.value
        props.dispatch(updateNewMessageTextActionCreator(text))
    }
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogElements}
            </div>
            <div className={s.messages}>
                {dialogMessages}
                <textarea placeholder="Enter message" onChange={onMessageTextChange} ref={newMessage} value={props.state.newMessageText} />
                <button onClick={addMessage}>Post</button>
            </div>
        </div>
    );
}
export default Dialog
