import styles from './Paginator.module.css'
import React, {useState} from 'react'

export const Paginator = ({totalUsersCount, pageSize, currentPage, onPageChanged}) => {
    let pagesCount = Math.ceil(totalUsersCount / pageSize)
    const [pages, setPages] = useState(0)
    const [minPage, setMinPage] = useState(0)
    const [maxPage, setMaxPage] = useState(7)
    const [clickbleMin, setClickbleMin] = useState(false)
    const [clickbleMax, setClickbleMax] = useState(true)
    for (let i = 1; i <= pagesCount; i++) {
        setPages(pages.push(i))
    }
    let visiblePages = pages
    if (visiblePages > 8) {
        visiblePages.slice(minPage, maxPage)
    }
    let prevPages = () => {
        if(minPage===0){
            setClickbleMin(false)
        }
        else setClickbleMin(true)
        setMinPage(prevState => prevState-1)
        setMaxPage(prevState => prevState-1)
    }
    let nextPages = () =>{
        if(maxPage===pages.length-1){
            setClickbleMax(false)
        }
        else setClickbleMax(true)
        setMinPage(prevState => prevState+1)
        setMaxPage(prevState => prevState+1)
    }
    return <div className={styles.pageBlock}>
        <button onClick={prevPages} disabled={clickbleMin}>prev</button>
        {pages.map(p => <span className={currentPage === p && styles.selectedPage}
                              onClick={() => onPageChanged(p)}>{p}</span>)}
        <button onClick={nextPages} disabled={clickbleMax}>next</button>
    </div>
}
