import React from 'react'
import Profile from "./Profile";
import {connect} from "react-redux";
import {getStatus, savePhoto, selectedUser, updateStatus} from '../../redux/profileReducer'
import {withRouter} from "react-router-dom";
import {compose} from "redux";

class ProfileContainer extends React.Component{
    updating =()=>{
        this.updateMode?this.updateMode = false:setUpdateMode(true)
    }
    refreshProfile(){
        let userId = this.props.match.params.userId
        if(!userId){
            userId = this.props.authorizedUserId
            if(!userId){
                this.props.history.push('/login')
            }
        }
        this.props.selectedUser(userId)
        this.props.getStatus(userId)
    }
    componentDidMount() {
        this.refreshProfile()
    }
    componentDidUpdate(prevProps, prevState, snapshot) {
        if(prevProps===this.props && prevState===this.state){

        }else {
            this.refreshProfile()
        }
    }

    render() {
        return<>
            <Profile {...this.props}
                    
                     profile ={this.props.profile}
                     isOwner={!this.props.match.params.userId}
                     status = {this.props.status}
                     updateStatus = {this.props.updateStatus}
                     savePhoto = {this.props.savePhoto}/>
        </>
    }
}

let mapStateToProps = (state) => ({
    profile: state.profilePage.profile,
    status: state.profilePage.status,
    authorizedUserId: state.auth.userId,
    isAuth: state.auth.isAuth,
    updateMode: false
})

export default compose(
    connect(mapStateToProps,{selectedUser, getStatus, updateStatus, savePhoto}),
    withRouter
)(ProfileContainer)
